mapper.select:
SELECT
ID,RESOURCE_TYPE,RESOURCE_NAME,RESOURCE_LOCATION,RESOURCE_COUNT,RESOURCE_DURATION,FACTORY
FROM T_MES_KANBAN_RESOURCE

    ID=#{id}
    AND FACTORY=#{factory}
    AND RESOURCE_TYPE LIKE #{type}
    AND RESOURCE_NAME LIKE #{name}

ORDER BY CREATION_TIME DESC

SELECT
    ID,PAGE_NAME,PAGE_RESOURCES,CYCLE_INTERVAL,PAGE_TIMING,FACTORY,PAGE_VERSION
FROM T_MES_KANBAN_PAGE

    ID=#{id}
    AND FACTORY=#{factory}
    AND PAGE_NAME LIKE #{name}

ORDER BY CREATION_TIME DESC
